package com.deedee.apigatewayv2.controller;import com.deedee.apigatewayv2.model.Product;import com.deedee.apigatewayv2.workmanager.workManager;import org.springframework.web.bind.annotation.*;import java.io.UnsupportedEncodingException;import java.net.URLDecoder;import java.util.HashMap;@RestController@RequestMapping(value = "/api-gateway", method = {RequestMethod.POST, RequestMethod.GET, RequestMethod.PUT, RequestMethod.DELETE})public class gatewayController {    @RequestMapping(value = "")    public HashMap<String, Product> Index(            @RequestParam("id") String id,            @RequestParam(value = "data", required = false) String[] param,            @RequestParam(value = "json", required = false) String json,            @RequestBody(required = false) Product product    ) throws UnsupportedEncodingException {        String ob = URLDecoder.decode(json, "UTF-8");        if(param == null){            param = new String[]{""};        }        return new workManager(id).getWork(param, product, ob);    }}